import React from "react";
import Head from "next/head";
import Prismic from "prismic-javascript";
import { RichText } from "prismic-reactjs";

// Project components & functions
import DefaultLayout from "layouts";
import { Header, Card} from "../components";
import { Client } from "utils/prismicHelpers";

/**
 * Homepage component
 */
const Home = ({ doc, posts }) => {
  if (doc && doc.data) {
    return (
      <DefaultLayout>
        <Head>
          <title>{RichText.asText(doc.data.headline)}</title>
        </Head>
        <Card card={doc.data.card} />
      </DefaultLayout>
    );
  }
  return null;
};

export async function getStaticProps({ preview = null, previewData = {} }) {

  const { ref } = previewData
  const client = Client()
  const doc = await client.getSingle("card", { fetchLinks: ['article.title','article.cover','article.summary']}) || {}
  const { menu, footer } = await load_menus(client)

  return {
    props: {
      doc,
      menu,
      footer,
      preview
    },
    revalidate: 60
  }
}

export default Home;
